import React, { Component } from 'react';
import "./css/bike.css";

class Bike extends Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
        this.state = { rented: false };
    }

    handleClick() {
        this.setState({ rented: true })
    }

    showAlert() {
        alert('Clicked');
    }

    render() {
        let article;

        if (this.state.rented) {
            article = <article className="bicycle">
                <h2>This bike is rented</h2>
            </article>
        }
        else {
            article = <article className="bicycle">
                <h2>{this.props.title}</h2>
                <p>{this.props.city}</p>
                <p>available from {this.props.startDate} to {this.props.endDate}</p>
                <p>price: {this.props.price} kr / day</p>
                <button onClick={this.handleClick}>
                    Yes please!
                </button>
                <button onClick={this.showAlert}>Show avaible dates</button>
            </article>
        }
        return article;
    }
}

export default Bike;
