import React, { Component } from 'react';
import "./css/mainSearchBox.css";

import 'react-dates/initialize';
import { DateRangePicker, SingleDatePicker, DayPickerRangeController } from 'react-dates';
import 'react-dates/lib/css/_datepicker.css';
import ItemList from './ItemList';

import {Route, Link} from 'react-router-dom';

class MainSearchBox extends Component {

  constructor(props) {
    super(props);
    this.state = {city: '', type:''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const target = event.target;
    this.setState({city: target.value});

  }

  handleSelect(event){
    const target = event.target;
    this.setState({type: target.value});
    console.log(this.state);
  }

  handleSubmit(event) {
    // alert('Searched: ' + 'CITY: ' +  this.state.city + 'START DATE: ' + this.state.startDate.toString()
    // + 'END DATE: '+ this.state.endDate.toString() + 'TYPE: ' + this.state.type );
    // this.props.history.push('/Items');
    event.preventDefault();
  }

  render(){
    const calendarChange =  title => (...args) => console.log(title,args);
    return(
      <div class="search">
      <h1> BOOK </h1>

      <form onSubmit={this.handleSubmit}>
      <div class="city_and_submit">
      <input type="text" value={this.state.city} onChange={event => {event.preventDefault();
        this.handleChange(event);}}
        placeholder="City" />
        </div>

        <div class="date">
        <DateRangePicker
        startDate={this.state.startDate} // momentPropTypes.momentObj or null,
        startDateId="your_unique_start_date_id" // PropTypes.string.isRequired,
        endDate={this.state.endDate} // momentPropTypes.momentObj or null,
        endDateId="your_unique_end_date_id" // PropTypes.string.isRequired,
        onDatesChange={({ startDate, endDate }) => this.setState({ startDate, endDate })} // PropTypes.func.isRequired,
        focusedInput={this.state.focusedInput} // PropTypes.oneOf([START_DATE, END_DATE]) or null,
        onFocusChange={focusedInput => this.setState({ focusedInput })} // PropTypes.func.isRequired,
        minimumNights={0}
        />
        </div>

        <div class="type">
        <label>

        Type

        <select type="text" value={this.state.type} onChange={event => {event.preventDefault(); this.handleSelect(event);}} >
        <option selected value="mtb">Mountain Bike</option>
        <option value="hybrid">Hybrid Bike</option>
        <option value="city">City Bike</option>
        <option value="electric">Electric Bike</option>

        </select>
        </label>
        </div>
        <div class="city_and_submit">
        <Link to='/Items' >
        <input type="submit" value="Search" />
        </Link>
        </div>
        </form>

        </div>
      )
    }
  }
  export default MainSearchBox;
